package SelfPractice.PracticeatHome;

public class EvenOddNumbers {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		// Even Numbers
	
		for (int i=0; i<100; i++) {
			if(i%2==0) {
				System.out.println(i);
			}
		}
		
		// Odd Numbers
		
	for (int i=1; i<100; i++) {
		if (i%2!=0) {
			System.out.println(i);
		
		}
		}
		}
	}
